mapper.select:
SELECT point
FROM member
WHERE MemberID = #{memberID};

mapper.update:
   		UPDATE runninggoo 
		SET MemberPendingStatusYN = 'N'
		WHERE memberid = #{memberid}
		AND roomnumber = #{roomnumber}

mapper.delete:
DELETE FROM runninggoo
WHERE memberid = #{memberid}
AND roomnumber = #{roomnumber}
